kdoc.example:
unit main;

{$mode objfpc}{$H+}

interface

uses
  Classes, SysUtils, Forms, Controls, Graphics, Dialogs, ExtCtrls, StdCtrls, math;

type

  { TCardiacForm }

  TCardiacForm = class(TForm)
    MaxSpeed: TCheckBox;
    Label3: TLabel;
    Output: TListBox;
    StepCardiac: TButton;
    RunCardiac: TButton;
    LoadDeck: TButton;
    CardiacLogo: TImage;
    Label1: TLabel;
    Label2: TLabel;
    Reader: TListBox;
    Deck: TMemo;
    MemoryGroup: TGroupBox;
    procedure FormCreate(Sender: TObject);
    procedure LoadDeckClick(Sender: TObject);
    procedure RunCardiacClick(Sender: TObject);
    procedure StepCardiacClick(Sender: TObject);
  private
    Memory: Array of TEdit;
    FPC: Byte;
    Acc: Integer;
    Running: Boolean;
    procedure CreateMemory;
    procedure ResetMemory;
    function GetInput: string;
    function GetMemory(addr: Byte): string;
    function GetMemoryInt(addr: Byte): Integer;
    procedure SetMemory(addr: Byte; value: Integer);
    procedure SetMemory(addr: Byte; value: String);
    procedure ProcessOp;
    procedure SetPC(const value: Byte);
    property PC: Byte read FPC write SetPC;
  public

  end;

var
  CardiacForm: TCardiacForm;

implementation

{$R *.lfm}

{ TCardiacForm }

procedure TCardiacForm.FormCreate(Sender: TObject);
begin
  FPC:=0;
  Acc:=0;
  CreateMemory;
  ResetMemory;
  Deck.Text:='';
end;

procedure TCardiacForm.LoadDeckClick(Sender: TObject);
var
  i, bc: Integer;
  line: String;
begin
  Reader.Items.Clear;
  for i:=0 to Deck.Lines.Count-1 do
  begin
    line:=Deck.Lines.Strings[i];
    if Length(line) <> 3 then
    begin
      if Length(line) = 0 then
        Exit;
      ShowMessage('Line is not 3 character: '+line);
      Exit;
    end
    else if TryStrToInt(line, bc) = False then
    begin
      ShowMessage('Lines should only contain numbers: '+line);
      Exit;
    end;
    Reader.Items.Add(line);
  end;
end;

procedure TCardiacForm.RunCardiacClick(Sender: TObject);
begin
  Output.Items.Clear;
  PC:=0;
  Running:=True;
  repeat
    ProcessOp;
    Refresh;
    if MaxSpeed.Checked = False then
      Sleep(100);
    if PC > 99 then
      Running:=False;
  until Running = False;
end;

procedure TCardiacForm.StepCardiacClick(Sender: TObject);
begin
  if PC > 99 then
    PC:=0;
  ProcessOp;
end;

procedure TCardiacForm.CreateMemory;
var
  i: Byte; // We don't need more than 100.
  row, col: Integer;
begin
  SetLength(Memory, 100);
  row:=10;
  col:=10;
  for i:=0 to 99 do
  begin
    Memory[i]:=TEdit.Create(MemoryGroup);
    Memory[i].SetBounds(col,row,60,24);
    Memory[i].ReadOnly:=True;
    Memory[i].Text:='';
    Memory[i].MaxLength:=3;
    Memory[i].Parent:=MemoryGroup;
    row:=row+25;
    if row > (25*10) then
    begin
      row:=10;
      col:=col+65;
    end;
  end;
end;

procedure TCardiacForm.ResetMemory;
begin
  Memory[0].Text:='001';
  Memory[99].Text:='8--';
  PC:=0;
  Running:=False;
end;

function TCardiacForm.GetInput: string;
begin
  if Reader.Count = 0 then
  begin
    Result:=InputBox('Cardiac Manual Input','Please enter a number?','');
    Exit;
  end;
  Result:=Reader.Items.Strings[0];
  Reader.Items.Text:=RightStr(Reader.Items.Text, Length(Reader.Items.Text)-4);
end;

function TCardiacForm.GetMemory(addr: Byte): string;
begin
  if addr > 99 then
    Exit;
  Result:=Memory[addr].Text;
end;

function TCardiacForm.GetMemoryInt(addr: Byte): Integer;
begin
  if TryStrToInt(GetMemory(addr), Result) = False then
    Result:=0;
end;

procedure TCardiacForm.SetMemory(addr: Byte; value: Integer);
begin
  SetMemory(addr, IntToStr(value));
end;

procedure TCardiacForm.SetMemory(addr: Byte; value: String);
begin
  if addr > 99 then
    Exit;
  Memory[addr].Text:=value;
end;

procedure TCardiacForm.ProcessOp;
var
  op, data, x, y, i: Byte;
  ir: Integer;
begin
  ir:=GetMemoryInt(PC);
  PC:=PC+1;
  op:=floor(ir div 100);
  data:=ir mod 100;
  case op of
    0: SetMemory(data, GetInput);
    1: Acc:=GetMemoryInt(data);
    2: Inc(Acc,GetMemoryInt(data));
    3: begin
      if Acc < 0 then
        PC:=data;
    end;
    4: begin
      x:=floor(data div 10);
      y:=data mod 10;
      for i:=0 to x-1 do
        Acc:=(Acc * 10) mod 10000;
      for i:=0 to y-1 do
        Acc:=floor(Acc div 10);
    end;
    5: Output.Items.Add(GetMemory(data));
    6: SetMemory(data, Acc);
    7: Dec(Acc, GetMemoryInt(data));
    8: begin
      SetMemory(99, PC+800);
      PC:=data;
    end;
    9: Running:=False;
  end;
end;

procedure TCardiacForm.SetPC(const value: Byte);
begin
  Memory[FPC].Color:=$FFFFFF;
  FPC:=value;
  Memory[FPC].Color:=$99FFCC;
end;

end.

section.example:
program casm;

{$mode objfpc}{$H+}

uses strutils, Classes, sysutils;

const
  BOOTLOADER: Array[0..9] of String = ('001','189','200','689','198','700','698','301','889','SIZE');

var
  addr, pc, size: Integer;
  buffer: TStringList;

function getString(var src: string; symb: char): string;
begin
  src:=RightStr(src, Length(src)-1);
  Result:=Copy2SymbDel(src, symb);
end;

function getToken(var src: string): string;
begin
  if src[1] = '"' then
    Result:=getString(src, '"')
  else
    Result:=Copy2SpaceDel(src);
end;

function generateOpMap: TStringList;
begin
  Result:=TStringList.Create;
  Result.DelimitedText:='inp,cla,add,tac,sft,out,sto,sub,jmp,hrs';
end;

function ptr: Integer;
begin
  if addr > -1 then
    Result:=addr
  else
    Result:=pc;
end;

procedure writeCards(card_list: Array of String);
var
  i: Integer;
begin
  for i:=0 to Length(card_list)-1 do
    buffer.Add(card_list[i]);
  pc:=pc+Length(card_list);
end;

procedure writeAddr;
var
  a: String;
begin
  if addr > -1 then
  begin
    a:='000'+IntToStr(addr);
    writeCards([a]);
    Inc(addr);
  end;
  Inc(size);
end;

var
  sfile, op_map, var_cards, labels, ofile: TStringList;
  i, opcode: Integer;
  line, op, param, param2, start: String;
  value, a: Byte;
  final: Boolean;

begin
  if ParamCount = 0 then
  begin
    WriteLn('Missing file to assemble.');
    Halt(1);
  end;
  final:=False;
  op_map:=generateOpMap;
  buffer:=TStringList.Create;
  buffer.Delimiter:=#10;
  sfile:=TStringList.Create;
  sfile.LoadFromFile(ParamStr(1));
  var_cards:=TStringList.Create;
  labels:=TStringList.Create;
  start:='-1';
  addr:=-1;
  pc:=0;
  size:=0;
  for i:=0 to sfile.Count-1 do
  begin
    param:='';
    line:=sfile.Strings[i];
    if (line = '') or (line[1] = '#') then
      Continue;
    op:=lowercase(getToken(line));
    if op = 'var' then
    begin
      param:=getToken(line);
      if getToken(line) <> '=' then
      begin
        WriteLn('Invalid var usage: ',sfile.Strings[i]);
        Break;
      end;
      param2:=getToken(line);
      if param2[1] = '*' then
        var_cards.Add(param+'='+param2)
      else
        var_cards.Add(param+'=000'+param2);
    end
    else if op = 'label' then
    begin
      param:=getToken(line);
      if labels.IndexOf(param) > -1 then
      begin
        WriteLn('Label has been declared twice: ',param);
        Break;
      end;
      labels.Add(param+'=00'+IntToStr(ptr));
    end
    else if op = 'bootstrap' then
    begin
      addr:=3;
      start:='3';
      writeCards(['002','800']);
      pc:=3;
    end
    else if op = 'bootloader' then
    begin
      writeCards(['002','800']);
      a:=89;
      for value:=0 to 9 do
      begin
        writeCards(['0'+IntToStr(a), BOOTLOADER[value]]);
        Inc(a);
      end;
      writeCards(['002','889']);
      pc:=1;
    end
    else if op = 'end' then
      final:=True;
    opcode:=op_map.IndexOf(op);
    if opcode > -1 then
    begin
      if line <> '' then
      begin
        param:=getToken(line);
        if param[1] = '$' then
          param:='*'+RightStr(param, Length(param)-1)
        else
          param:='000'+param
      end;
      if pc = StrToInt(start) then
        start:=IntToStr(opcode)+param
      else
      begin
        writeAddr;
        writeCards([IntToStr(opcode)+param]);
      end;
    end;
  end;
  if final = True then
  begin
    ofile:=TStringList.Create;
    ofile.Delimiter:=#10;
    for i:=0 to var_cards.Count-1 do
    begin
      param:='000'+IntToStr(ptr);
      writeAddr;
      op:=var_cards.ValueFromIndex[i];
      if op[1] <> '*' then
        writeCards([RightStr(op,3)])
      else
        writeCards(['1'+op]);
      labels.Add(var_cards.Names[i]+'='+RightStr(param,3));
    end;
    if start <> '-1' then
      writeCards(['002',start]);
    for i:=0 to buffer.Count-1 do
    begin
      op:=buffer.Strings[i];
      if op[2] = '*' then
      begin
        param:=RightStr(op,Length(op)-2);
        ofile.Add(op[1]+RightStr(labels.Values[param],2));
      end
      else if RightStr(op,4) = 'SIZE' then
      begin
        param:='000'+IntToStr(size-1);
        ofile.Add(RightStr(param,3));
      end
      else
        if Length(op) = 1 then
          ofile.Add(op+'00')
        else
          ofile.Add(RightStr('000'+op,3));
    end;
    for i:=0 to ofile.Count-1 do
      WriteLn(ofile.Strings[i]);
    ofile.Free;
  end;
  var_cards.Free;
  labels.Free;
  sfile.Free;
  op_map.Free;
  buffer.Free;
end.
